mapper.select:
    	    select 
			
 			from staff 
    	    
    	        
    	  	    
    	  	    	and STAFF_CODE = #{staffCode}
    	  	      
    	  	    
    	  	    	and staff_name like #{staffName}
   	  	      	
    	  	    
    	  	    	and lan_id = #{lanId,jdbcType=INTEGER}
    	  	     
    	  	    
    	  	    	and staff_type = #{staffType,jdbcType=INTEGER}
